import React from "react";
import ReactDom from "react-dom";
//CSS
import './index.css';

const firstbook={
  title: "Ikigai",
  author: "Francesc Mirralles",
  img: "https://n2.sdlcdn.com/imgs/k/h/f/850X995_sharpened_2_1/Ikigai-book-SDL307452525-1-97bcb.webp"
}
const secondbook={
  title: "The Silent Patient",
  author: "Alex Michaelides",
  img: "https://images-eu.ssl-images-amazon.com/images/I/81JJPDNlxSL._AC_UL900_SR900,600_.jpg"
}

function BookList(){
 return(
  <section className="booklist">
    <Book title={firstbook.title} author={firstbook.author} img={firstbook.img}>
      <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Eaque tenetur accusamus omnis ipsam quas exercitationem a laboriosam itaque blanditiis est?</p>
    </Book>

    <Book title={secondbook.title} author={secondbook.author} img={secondbook.img}/>
 </section>
 ) ;
}

const Book=({img,title,author,children})=>{
  //const {img,title,author}=props;
  return <article className="book">
    <img src={img} height="300"></img>
    <h2>{title}</h2>
    <h3>{author}</h3>
    {children}
  </article>;
};


ReactDom.render(<BookList/>,document.getElementById('root'))
